mapper.select:
select *
from nx_distributer_goods
where nx_dg_distribute_id = #{disId}
  and nx_dg_nx_goods_father_id = #{nxFatherId}

select *
from nx_distributer_goods
where nx_dg_distribute_id = #{disId}
  and nx_dg_nx_goods_id = #{goodsId}

select count(*)
from nx_distributer_goods
where nx_dg_nx_goods_grand_id = #{nxGrandGoodsId}

select count(*)
from nx_distributer_goods
where nx_dg_nx_goods_father_id = #{nxGrandGoodsId}

select *
from nx_distributer_goods where (nx_dg_goods_py like concat('%', #{str}, '%')
                                   or nx_dg_goods_pinyin like concat('%', #{str}, '%')
                                   or nx_dg_goods_name like concat('%', #{str}, '%'));
